import { Field, useFormik } from "formik";
import React, { useContext } from "react";
import {
  BoldLink,
  BoxContainer,
  FormContainer,
  Input,
  MutedLink,
  SubmitButton,
} from "./common";
import { Marginer } from "../marginer";
import { AccountContext } from "./accountContext";
//import * as yup from "yup";
//import axios from "axios";

export function SignupForm(props) {
  const { switchToSignin } = useContext(AccountContext);

  const onSubmit = (values) => {
    alert(JSON.stringify(values));
  };
const formik = useFormik( {initialValues: {nombre: "", apellido: "", correo: "", telefono: "", direccion: "", pass: "", passConfirmation: ""},
  validateOnBlur: true,
  onSubmit,
});
  return (
    <BoxContainer>
      <FormContainer onSubmit={formik.handleSubmit}>
  
         <Input icon="user" type="text" name="nombre" id="nombre" placeholder="Nombre" value={formik.values.nombre} onChange={formik.handleChange}/>
        <Input type="text" name="apellido" id="apellido" placeholder="Apellido" value={formik.values.apellido} onChange={formik.handleChange}/>
        <Input type="email" name="correo" id="correo" placeholder="Correo" value={formik.values.correo} onChange={formik.handleChange}/>
        <Input type="text" name="telefono" id="telefono" placeholder="Telefóno" value={formik.values.telefono} onChange={formik.handleChange}/>
        <Input type="texto" name="direccion" id="direccion" placeholder="Dirección" value={formik.values.direccion} onChange={formik.handleChange}/>
        <Input type="password" name="pass" id="pass"  placeholder="Contraseña" value={formik.values.pass} onChange={formik.handleChange} />
        <Input type="password" name="passConfirmation" placeholder="Confirmar Contraseña" value={formik.values.passConfirmation} onChange={formik.handleChange}/>
      </FormContainer>
      <Marginer direction="vertical" margin={25} />
      <SubmitButton type="submit">Registrarse</SubmitButton>
      <Marginer direction="vertical" margin="1em" />
      <MutedLink href="#">
      ¿Ya tienes una cuenta?
        <BoldLink href="#" onClick={switchToSignin}>
        
        Iniciar sesión
        </BoldLink>
      </MutedLink>
    </BoxContainer>
  );
}
